import React, { useEffect, useState } from 'react';
import './rowposter.css';
import axios from 'axios';
import { baseUrl, API_KEY } from '../../constants/Constants';

const Rowposter = () => {
  const [posters, setPosters] = useState([]);

  useEffect(() => {
    axios.get(`${baseUrl}/discover/tv?api_key=${API_KEY}&with_networks=213`)
      .then(response => {
        console.log(response.data);
        setPosters(response.data.results);
      })
      .catch(err => {
        console.error(err);
        alert('Error fetching data');
      });
  }, []);

  return (
    <div className='row'>
      <h4>Your Next Watch</h4>
      <div className='posters'>
        {posters.map(poster => (
          <img 
            key={poster.id} 
            src={`https://image.tmdb.org/t/p/w500${poster.poster_path}`} 
            alt={poster.name || poster.title} 
          />
        ))}
      </div>
    </div>
  );
};

export default Rowposter;
