import React, { useEffect, useState } from 'react';
import './rowposter.css';
import axios from 'axios';
import { API_KEY, baseUrl } from '../../constants/Constants';

const Rowposter = () => {
  const [movies, setMovies] = useState([]); // Moved useState to the top level

  useEffect(() => {
    axios.get(`${baseUrl}/discover/tv?api_key=${API_KEY}&with_networks=213`)
      .then((response) => {
        console.log(response.data);
        setMovies(response.data.results);
      })
      .catch((err) => {
        console.error(err);
        // alert("error")
      });
  }, []);

  return (
    <div className='row'>
      <h4>Netflix Originals</h4>
      <div className='posters'>
        {movies.map((movie) => (
          <img key={movie.id} src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} alt={movie.name || movie.title} />
        ))}
      </div>
    </div>
  );
};

export default Rowposter;
